import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateTaskPopup = ({modal, toggle, save}) => {
    const [taskName, setTaskName] = useState('');
    const [description, setDescription] = useState('');

    const handleChange = (e) => {

        const {name, value} = e.target

        if(name === "taskName"){
            setTaskName(value)
        }else{
            setDescription(value)
        }


    }

    const handleSave = (e) => {
        e.preventDefault()
        let taskObj = {}
        taskObj["Name"] = taskName
        taskObj["Description"] = description
        save(taskObj)

    }

    return (
        <Modal isOpen={modal} toggle={toggle}>
            <ModalHeader toggle={toggle}>Create Task</ModalHeader>
            <ModalBody>


                    <div className = "form-group">
                        <label>Description</label>
                        <textarea rows = "5" className = "form-control" value = {description} onChange = {handleChange} name = "description" required></textarea>
                    </div>


                    <form>
                <div class="mb-3">
                    <label for="taskname" class="form-label">Task Name</label>
                    <input type="text" class="form-control" id="taskname" value = {taskName} onChange = {handleChange} name = "taskName" required/>
                </div>
                <div class="mb-3">
                    <label for="Description" class="form-label">Description</label>
                    <input type="text" class="form-control" id="Description" value = {description} onChange = {handleChange} name = "description" required/>
                </div>
                <div class="mb-3 form-check">
                </div><Button color="primary" onClick={handleSave}  type="submit">Create</Button>{' '}
            <Button color="secondary" onClick={toggle}>Cancel</Button>
                </form>
            </ModalBody>

      </Modal>
    );
};

export default CreateTaskPopup;
