import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateTaskPopup = ({modal, toggle, save}) => {
    const [taskName, setTaskName] = useState('');
    const [description, setDescription] = useState('');
    var [time] = useState(new Date());
    const handleChange = (e) => {

        const {name, value} = e.target

        if(name === "taskName"){
            setTaskName(value)
        }else{
            setDescription(value)
        }


    }

    const handleSave = (e) => {
        e.preventDefault()
        let taskObj = {}
        taskObj["Name"] = taskName
        taskObj["Description"] = description
        taskObj["time"]= time.toLocaleTimeString()
        save(taskObj)

    }

    return (
        <Modal isOpen={modal} toggle={toggle}>
            <ModalHeader toggle={toggle}>Create Task</ModalHeader>
            <ModalBody>
                    <form>
                <div class="mb-3">
                    <label for="taskname" class="form-label">Task Name</label>
                    <input type="text" class="form-control" id="taskname" value = {taskName} onChange = {handleChange} name = "taskName" required/>
                </div>
                <div class="mb-3">
                    <label for="Description" class="form-label">Description</label>
                    <input type="text" class="form-control" id="Description" value = {description} onChange = {handleChange} name = "description" required/>
                </div>
                <button color="primary" onClick={handleSave}  type="submit" style={{borderRadius:'10px', margin:'5px',padding:'10px',backgroundColor:'blue',borderColor:'blue',color:"white"}}>Create</button>{' '}
                <button color="secondary" onClick={toggle} style={{borderRadius:'10px', margin:'5px',padding:'10px',backgroundColor:'blue',borderColor:'blue',color:"white"}}>Cancel</button>
                </form>
            </ModalBody>

      </Modal>
    );
};

export default CreateTaskPopup;
